
public class OperatorEx7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

//		논리 연산자
		
		int n = 10;
		
		System.out.println(n++ >= 20 && n++ <= 20);
		System.out.println(n);
		n = 10;
		System.out.println(n++ >= 20 & n++ <= 20);
		System.out.println(n);
//		
//		System.out.println(n >= 20 || n <= 20);
//		System.out.println(n >= 20 | n <= 20);
		
		
	}

}
