
public class OperatorEx8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

//		단항 연산자 ++,, --
//		증감 연산자
		int n = 5;
		n++;
//		n = n + 1;
		System.out.println(n);
		
		n = 5;
		++n;
//		n = n + 1;
		System.out.println(n);
		
		n--;
		System.out.println(n);
		
	}

}
